mapper.select:
SELECT id, user_id, username, module, operation, method, params, ip, location,
       browser, os, status, error_msg, cost_time, operate_time
FROM system_log


        AND user_id = #{query.userId}


        AND username LIKE CONCAT('%', #{query.username}, '%')


        AND module = #{query.module}


        AND operation = #{query.operation}


        AND ip = #{query.ip}


        AND status = #{query.status}


        AND operate_time >= #{query.startTime}


        AND operate_time <= #{query.endTime}


ORDER BY operate_time DESC

SELECT
    COUNT(*) AS total_count,
    COUNT(CASE WHEN status = 1 THEN 1 END) AS success_count,
    COUNT(CASE WHEN status = 0 THEN 1 END) AS failure_count,
    COALESCE(SUM(cost_time), 0) AS total_cost_time,
    COALESCE(AVG(cost_time), 0) AS avg_cost_time
FROM system_log


        AND user_id = #{query.userId}


        AND username LIKE CONCAT('%', #{query.username}, '%')


        AND module = #{query.module}


        AND operation = #{query.operation}


        AND ip = #{query.ip}


        AND status = #{query.status}


        AND operate_time >= #{query.startTime}


        AND operate_time <= #{query.endTime}
