mapper.select:
SELECT * FROM purchase_receipt

    deleted = 0

        AND receipt_code LIKE CONCAT('%', #{receiptCode}, '%')


        AND order_id = #{orderId}


        AND supplier_id = #{supplierId}


        AND receipt_status = #{receiptStatus}


        AND receipt_date >= #{startDate}


        AND receipt_date <= #{endDate}


ORDER BY create_time DESC
